import React from 'react';
import { Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle} from 'reactstrap';
import { Loading } from './LoadingComponent';
import { baseUrl } from '../shared/baseUrl';
import { FadeTransform } from 'react-animation-components';

function RenderCard({item, isLoading, errMess}) {

    if (isLoading) {
        return(
                <Loading />
        );
    }
    else if (errMess) {
        return(
                <h4>{errMess}</h4>
        );
    }


      else
      {
        var i
        for (i in item)
          return(
            <FadeTransform
                in
                transformProps={{
                    exitTransform: 'scale(0.5) translateY(-50%)'
                }}>
                <Card>
                    <CardBody className="bg-faded">
                        <blockquote className="blockquote">
                            <p className="mb-0">{item[0].device_name}  ({item[0].id})</p>

                            <cite title="Source Title">Brand : {item[0].hardwareInfo.brand}</cite><br></br>
                            <cite title="Source Title"> Model : {item[0].hardwareInfo.model}</cite><br></br>
                            <cite title="Source Title">Serial Number : {item[0].hardwareInfo.serialNumber}</cite><br></br>
                            <cite title="Source Title">Total Ram : {item[0].memoryInfo.totalRam}</cite><br></br>
                            <cite title="Source Title">Total Internal Storage Ram : {item[0].memoryInfo.totalInternalStorage}</cite><br></br>



                        </blockquote>
                    </CardBody>
                </Card>
              </FadeTransform>
          );

  }


}

function Home(props) {
    return(
        <div className="container">
            <div className="row align-items-start">
                <div className="col-12 col-md m-1">
                    <RenderCard item={props.device.results} isLoading={props.deviceLoading} errMess={props.deviceErrMess}  />
                </div>
            </div>
        </div>
    );
}

export default Home;
