import React, {Component} from 'react';
import {Card, CardBody, CardText, CardImg, CardTitle} from 'reactstrap';


class DishDetail extends Component{

    render(){
        const dishItem = this.props.selectedDish ? (
          <Card>
          <CardImg top src={this.props.selectedDish.image} alt={this.props.selectedDish.name} />
          <CardBody>
            <CardTitle>{this.props.selectedDish.name}</CardTitle>
            <CardText>{this.props.selectedDish.description}</CardText>
          </CardBody>
      </Card>
        ) : (
          <div></div>
        );

        const heading = this.props.selectedDish ? (
          <div>
            <h2>Comments</h2>
          </div>
        ) : (
          <div></div>
        );

        const dishComments = this.props.selectedDish ? (
            this.props.selectedDish.comments.map(detail => {
              return(
                <div className="row m-1" key={detail.id}>
                  <ul className="list-unstyled">
                    <li>{detail.comment }</li><br />
                    <li>{"\n--"+ detail.author + ", " + detail.date + "\n"}</li>
                  </ul>
                </div>
              )
            }
            )
        ) : (

          <div></div>
        );

        return(
          <div className="row">
            <div className="col-12 col-md-5 m-1">{dishItem}</div>
        <div className="col-12 col-md-5 m-1"> {heading}{dishComments}</div>
          </div>
      );
    }
}

export default DishDetail;
