import React from 'react';
import { Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle} from 'reactstrap';
import { Loading } from './LoadingComponent';
import { baseUrl } from '../shared/baseUrl';
import { FadeTransform } from 'react-animation-components';

function RenderCard({item, isLoading, errMess}) {

    if (isLoading) {
        return(
                <Loading />
        );
    }
    else if (errMess) {
        return(
                <h4>{errMess}</h4>
        );
    }


      else
      {
        var i
        for (i =0;i<item.length;i++)
        {
          return(
            <FadeTransform
                in
                transformProps={{
                    exitTransform: 'scale(0.5) translateY(-50%)'
                }}>
                <Card>
                    <CardBody className="bg-faded">
                        <blockquote className="blockquote">
                            <p className="mb-0">{item[i].device_name}  ({item[i].id})</p>

                            <cite title="Source Title">Brand : {item[i].hardwareInfo.brand}</cite><br></br>
                            <cite title="Source Title">Model : {item[i].hardwareInfo.model}</cite><br></br>
                            <cite title="Source Title">Serial Number : {item[i].hardwareInfo.serialNumber}</cite><br></br>
                            <cite title="Source Title">Total Ram : {item[i].memoryInfo.totalRam}</cite><br></br>
                            <cite title="Source Title">Total Internal Storage Ram : {item[i].memoryInfo.totalInternalStorage}</cite><br></br>



                        </blockquote>
                    </CardBody>
                </Card>
              </FadeTransform>
          );

  }
}


}

function Home(props) {
    return(
        <div className="container">
            <div className="row align-items-start">
                <div className="col-12 col-md m-1">
                    <RenderCard item={props.device.results} isLoading={props.deviceLoading} errMess={props.deviceErrMess}  />
                </div>
            </div>
        </div>
    );
}

export default Home;
